function showElementByTag(tag){
    $(tag).show()
}

function hideElementByTag(tag){
    $(tag).hide()
}

function showElementByClass(classe){
    $('.'+classe).show()
}

function hideElementByClass(classe){
    $('.'+classe).hide()
}

function showElementById(id){
    $('#'+id).show()
}

function hideElementById(id){
    $('#'+id).hide()
}